package practise_New;

public class Conditional_statements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char a='i';
		switch(a) {
		case 'a':
			System.out.println(a+" is a vowel");
			break;
		case 'e':
			System.out.println(a+" is a vowel");
			break;
		case 'i':
			System.out.println(a+" is a vowel");
			break;
		case 'o':
			System.out.println(a+" is a vowel");
			break;
		case 'u':
			System.out.println(a+" is a vowel");
			break;
		default:
			System.out.println(a+" is not a vowel");
			break;
		}

	}

}
